/** @format */

import React,{useState} from 'react';
import '../styles/app.css';
import AppButton from './AppButton';
import AppInput from './AppInput';

export default function App() {

  const users =[
    {
      id:1,
      username:"sika",
      password:"password",
    },
    {
      id:2,
      username:"kwame",
      password:"kwamepassword",
    },
    {
      id:1,
      username:"kojo",
      password:"password",
    },
  ]
  const [username, setUsername]=useState(null);
  const [password, setPassword]=useState(null);
  const usernameInputHandler =(data)=>{setUsername(data.target.value)}
  const passwordInputHandler =(data)=>{setPassword(data.target.value)}
  const buttonHandler =(event)=>{
    event.preventDefault();
    console.log(username,password)
  }

  return (
    <div className='login-page'>
      <div className='form'>
        <form className='login-form'>
            <AppInput  type='text' placeholder='username' onChange={usernameInputHandler}/>
            <AppInput type='password' placeholder='password' onChange={passwordInputHandler}/>
          {/* <input type='text' placeholder='username' />
          <input type='password' placeholder='password' /> */}
          <AppButton label="login" onClick={buttonHandler}/>
          <AppButton  label="create user" id='danger'/>
          <AppButton  label="recive" id='info'/>
          <p className='message'>
            Not registered? <a href='#'>Create an account</a>
          </p>
        </form>
      </div>
    </div>
  );
}
